import React, { useRef, useEffect } from 'react';
import RealTimeDataDisplay from './RealTimeDataDisplay';
import './dashboard.css'; 
import { Line } from 'react-chartjs-2';

function Dashboard({ labels, unitsConsumed, current, voltage }) {
    const consumedUnitsRef = useRef(null);
    const currentRef = useRef(null);
    const voltageRef = useRef(null);

    const generateChartData = (label, data, color) => ({
        labels: labels || [],
        datasets: [
            {
                label: label,
                data: data || [],
                borderColor: color,
                fill: false
            }
        ]
    });

    useEffect(() => {
        const consumedUnitsInstance = consumedUnitsRef.current;
        return () => {
            if (consumedUnitsInstance) {
                consumedUnitsInstance.chartInstance.destroy();
            }
        };
    }, [labels, unitsConsumed]);

    useEffect(() => {
        const currentInstance = currentRef.current;
        return () => {
            if (currentInstance) {
                currentInstance.chartInstance.destroy();
            }
        };
    }, [labels, current]);

    useEffect(() => {
        const voltageInstance = voltageRef.current;
        return () => {
            if (voltageInstance) {
                voltageInstance.chartInstance.destroy();
            }
        };
    }, [labels, voltage]);

    return (
        <div className="dashboard-container">
            <h2>Electricity Data Dashboard</h2>
            <RealTimeDataDisplay />

            <div className="chart-section">
                <div className="chart-container">
                    <Line ref={consumedUnitsRef} data={generateChartData('Consumed Units', unitsConsumed, '#FF5733')} />
                </div>
                <div className="chart-container">
                    <Line ref={currentRef} data={generateChartData('Current', current, '#33FF57')} />
                </div>
                <div className="chart-container">
                    <Line ref={voltageRef} data={generateChartData('Voltage', voltage, '#3357FF')} />
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
