import React, { useRef, useEffect } from 'react';
import RealTimeDataDisplay from './RealTimeDataDisplay';
import './dashboard.css'; 
import { Line } from 'react-chartjs-2';
import Chart from 'chart.js';

function Dashboard(props) {
    const consumedUnitsRef = useRef(null);
    const currentRef = useRef(null);
    const voltageRef = useRef(null);

    const generateChartData = (label, data, color) => ({
        labels: props.labels || [],
        datasets: [
            {
                label: label,
                data: data || [],
                borderColor: color,
                fill: false
            }
        ]
    });

    useEffect(() => {
        return () => {
            if (consumedUnitsRef.current) {
                consumedUnitsRef.current.chartInstance.destroy();
            }
            if (currentRef.current) {
                currentRef.current.chartInstance.destroy();
            }
            if (voltageRef.current) {
                voltageRef.current.chartInstance.destroy();
            }
        };
    }, [props]);

    return (
        <div className="dashboard-container">
            <h2>Electricity Data Dashboard</h2>
            <RealTimeDataDisplay />

            <div className="chart-section">
                <div className="chart-container">
                    <Line ref={consumedUnitsRef} data={generateChartData('Consumed Units', props.unitsConsumed, '#FF5733')} />
                </div>
                <div className="chart-container">
                    <Line ref={currentRef} data={generateChartData('Current', props.current, '#33FF57')} />
                </div>
                <div className="chart-container">
                    <Line ref={voltageRef} data={generateChartData('Voltage', props.voltage, '#3357FF')} />
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
