import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, off } from 'firebase/database';
import { firebaseConfig } from './firebaseConfig';
import Dashboard from './dataDisplay';
import './App.css';

// Initialize Firebase
initializeApp(firebaseConfig);

function App() {
    const [data, setData] = useState({
        labels: [],
        unitsConsumed: [],
        current: [],
        voltage: []
    });

    useEffect(() => {
        const db = getDatabase();
        const dbRef = ref(db);

        const handleDataChange = (snapshot) => {
            const currentTime = new Date().toLocaleTimeString(); // Using time as label for now
            setData(prevData => ({
                labels: [...prevData.labels, currentTime],
                unitsConsumed: [...prevData.unitsConsumed, snapshot.child('unitsConsumed').val()],
                current: [...prevData.current, snapshot.child('current').val()],
                voltage: [...prevData.voltage, snapshot.child('voltage').val()]
            }));
        };

        onValue(dbRef, handleDataChange);

        return () => {
            off(dbRef, handleDataChange);
        };
    }, []);

    return (
        <div className="app-container">
            <h1>Welcome to the Electricity Monitoring System</h1>
            <h2>Electricity Data Dashboard</h2>
            <Dashboard
                labels={data.labels}
                unitsConsumed={data.unitsConsumed}
                current={data.current}
                voltage={data.voltage}
            />
        </div>
    );
}

export default App;
