import React, { useState } from 'react';
import './App.css';

function App() {
  const [userName, setUserName] = useState('User Name');
  const [usagePattern, setUsagePattern] = useState('Normal');

  return (
    <div className="App">
      <div className="sidebar">
        <button className="btn">Logo</button>
        <button className="btn">Home</button>
        <button className="btn">Realtime Usage</button>
        <button className="btn">Payment</button>
        <button className="btn">Outage Calendar</button>
        <button className="btn">Profile</button>
        <button className="btn">FAQ</button>
      </div>

      <div className="main-content">
        <div className="top-section">
          <input type="date" />
          <div className="user-section">
            <div className="user-image"></div>
            {userName}
          </div>
        </div>

        <div className="usage-pattern">
          <h2>Your Current Usage Pattern:</h2>
          <p>{usagePattern}</p>
          <button onClick={() => setUsagePattern('High')}>Set to High</button>
          <button onClick={() => setUsagePattern('Low')}>Set to Low</button>
        </div>

        <h3>Kamal Fernando</h3>
      </div>
    </div>
  );
}

export default App;
