import React, { useState, useEffect } from 'react';
import firebase from 'firebase/app';
import 'firebase/database';
import './App.css';

function App() {
  const [userName, setUserName] = useState('Kamal Fernando');
  const [usagePattern, setUsagePattern] = useState('Normal');
  const [electricityData, setElectricityData] = useState({});

  useEffect(() => {
    const dbRef = firebase.database().ref(); // This references the root of your database

    // Listen for changes in the data
    const handleDataChange = snapshot => {
      setElectricityData(snapshot.val());
    };

    // Attach the listener
    dbRef.on('value', handleDataChange);

    // Cleanup listener on component unmount
    return () => {
      dbRef.off('value', handleDataChange);
    };
  }, []);

  // Step 1: Get the current date and format it
  const currentDate = new Date();
  const formattedDate = `${currentDate.getFullYear()}-${String(currentDate.getMonth() + 1).padStart(2, '0')}-${String(currentDate.getDate()).padStart(2, '0')}`;

  return (
    <div className="App">
      <div className="sidebar">
        {/* ... sidebar buttons ... */}
      </div>

      <div className="main-content">
        <div className="top-section">
          <input type="date" defaultValue={formattedDate} />
          <div className="user-section">
            <div className="user-image"></div>
            {userName}
          </div>
        </div>

        <div className="usage-pattern">
          <h2>Your Current Usage Pattern:</h2>
          <p>{usagePattern}</p>
          <button onClick={() => setUsagePattern('High')}>Set to High</button>
          <button onClick={() => setUsagePattern('Low')}>Set to Low</button>
        </div>

        <div className="realtime-data">
          <h2>Real-time Data:</h2>
          <p><strong>Consumed Units:</strong> {electricityData.unitsConsumed}</p>
          <p><strong>Current:</strong> {electricityData.current}</p>
          <p><strong>Voltage:</strong> {electricityData.voltage}</p>
          <p><strong>Cumulative Units:</strong> {electricityData.cumulativeUnits}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
