import React from 'react';
import { useFetchRealTimeData } from './useFetchRealTimeData';
import { Line } from 'react-chartjs-2';
import { Chart, LinearScale } from 'chart.js';
import { CategoryScale, TimeScale } from 'chart.js';  // Import TimeScale
import { PointElement } from 'chart.js';
import { LineElement } from 'chart.js';

Chart.register(LineElement);
Chart.register(PointElement);
Chart.register(CategoryScale);
Chart.register(LinearScale);
Chart.register(TimeScale);  // Register TimeScale

function RealTimeCharts() {
    const dataReadings = useFetchRealTimeData();

    const chartOptions = {
        responsive: true,
        scales: {
            x: {
                type: 'time',  // Set the x-axis to be a time scale
                title: {
                    display: true,
                    text: 'Timestamp'
                }
            },
            y: {
                beginAtZero: true,
                type: 'linear'
            }
        }
    };

    // Use the timestamp as the label
    const labels = dataReadings.map(item => new Date(item.timestamp));

    return (
        <div>
            <Line 
                data={{
                    labels: labels,
                    datasets: [{
                        label: 'Consumed Units',
                        data: dataReadings.map(item => item.unitsConsumed),
                        borderColor: 'rgba(75,192,192,1)',
                        borderWidth: 2,
                        fill: false
                    }]
                }}
                options={chartOptions}
            />
            <Line 
                data={{
                    labels: labels,
                    datasets: [{
                        label: 'Current',
                        data: dataReadings.map(item => item.current),
                        borderColor: 'rgba(255,0,0,1)',
                        borderWidth: 2,
                        fill: false
                    }]
                }}
                options={chartOptions}
            />
            <Line 
                data={{
                    labels: labels,
                    datasets: [{
                        label: 'Voltage',
                        data: dataReadings.map(item => item.voltage),
                        borderColor: 'rgba(0,0,255,1)',
                        borderWidth: 2,
                        fill: false
                    }]
                }}
                options={chartOptions}
            />
            <Line 
                data={{
                    labels: labels,
                    datasets: [{
                        label: 'Cumulative Units',
                        data: dataReadings.map(item => item.cumulativeUnits),
                        borderColor: 'rgba(0,255,0,1)',
                        borderWidth: 2,
                        fill: false
                    }]
                }}
                options={chartOptions}
            />
        </div>
    );
}

export default RealTimeCharts;
