import React from 'react';
import RealTimeDataDisplay from './RealTimeDataDisplay';
import './dashboard.css'; 
import { Line } from 'react-chartjs-2';
import { CategoryScale } from 'chart.js';
Chart.register(CategoryScale);
import { Chart } from 'chart.js';


function Dashboard(props) {
    const generateChartData = (label, data, color) => ({
        labels: props.labels || [],
        datasets: [
            {
                label: label,
                data: data || [],
                borderColor: color,
                fill: false
            }
        ]
    });

    return (
        <div className="dashboard-container">
            <h2>Electricity Data Dashboard</h2>
            <RealTimeDataDisplay />

            <div className="chart-section">
                <div className="chart-container">
                    <Line data={generateChartData('Consumed Units', props.unitsConsumed, '#FF5733')} />
                </div>
                <div className="chart-container">
                    <Line data={generateChartData('Current', props.current, '#33FF57')} />
                </div>
                <div className="chart-container">
                    <Line data={generateChartData('Voltage', props.voltage, '#3357FF')} />
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
