import React from 'react';
import { useFetchRealTimeData } from './useFetchRealTimeData';
import { Line } from 'react-chartjs-2';
import { Chart, LinearScale } from 'chart.js';
Chart.register(LinearScale);

function RealTimeCharts() {
    const dataReadings = useFetchRealTimeData();

    const chartOptions = {
        responsive: true,
        scales: {
            y: {
                beginAtZero: true,
                type: 'linear',
            }
        }
    };

    return (
        <div>
            {dataReadings.map((item, index) => (
                <div key={index}>
                    <Line 
                        data={{
                            labels: ['Data Point'],
                            datasets: [{
                                label: 'Consumed Units',
                                data: [item.unitsConsumed],
                                borderColor: 'rgba(75,192,192,1)',
                                borderWidth: 2,
                                fill: false
                            }]
                        }}
                        options={chartOptions}
                    />
                    <Line 
                        data={{
                            labels: ['Data Point'],
                            datasets: [{
                                label: 'Current',
                                data: [item.current],
                                borderColor: 'rgba(255,0,0,1)',
                                borderWidth: 2,
                                fill: false
                            }]
                        }}
                        options={chartOptions}
                    />
                    <Line 
                        data={{
                            labels: ['Data Point'],
                            datasets: [{
                                label: 'Voltage',
                                data: [item.voltage],
                                borderColor: 'rgba(0,0,255,1)',
                                borderWidth: 2,
                                fill: false
                            }]
                        }}
                        options={chartOptions}
                    />
                    <Line 
                        data={{
                            labels: ['Data Point'],
                            datasets: [{
                                label: 'Cumulative Units',
                                data: [item.cumulativeUnits],
                                borderColor: 'rgba(0,255,0,1)',
                                borderWidth: 2,
                                fill: false
                            }]
                        }}
                        options={chartOptions}
                    />
                </div>
            ))}
        </div>
    );
}

export default RealTimeCharts;
