import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, off } from 'firebase/database';
import { firebaseConfig } from './firebaseConfig';
import DataDisplay from './dataDisplay';
import RealTimeDataChart from './chartDisplay';

// Initialize Firebase
initializeApp(firebaseConfig);

function Dashboard() {
    const [data, setData] = useState({
        cumulativeUnits: 0,
        current: 0,
        unitsConsumed: 0,
        voltage: 0
    });

    useEffect(() => {
        const db = getDatabase();
        const dbRef = ref(db);

        const handleDataChange = (snapshot) => {
            const results = {
                cumulativeUnits: snapshot.child('cumulativeUnits').val(),
                current: snapshot.child('current').val(),
                unitsConsumed: snapshot.child('unitsConsumed').val(),
                voltage: snapshot.child('voltage').val(),
            };

            setData(results);
        };

        onValue(dbRef, handleDataChange);

        return () => {
            off(dbRef, handleDataChange);
        };
    }, []);

    return (
        <div>
            <h1>Real-Time Electricity Monitoring Dashboard</h1>
            <DataDisplay data={data} />
            <RealTimeDataChart data={data} />
        </div>
    );
}

export default Dashboard;
